<?php	function printLinkList($links,$node=1){				addTabs($node);		print "<ul>\n";		foreach($links as $label=>$value){			if(!is_array($value)){				addTabs($node+1);				print "<li><a href=\"$value\">$label</a></li>\n";			} else {				printLinkList($value,$node+1);			}		}		addTabs($node); 		print "</ul>\n";	}		function addTabs($tabs){		for($i=0; $i<$tabs; $i++){			print "\t";		}	}	?>